import React from 'react';
import PropTypes from 'prop-types';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Button } from 'reactstrap';
import Form from 'react-bootstrap/Form';
import { createUser } from '../services/userAPI';
import Loading from './Loading';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      nome: '',
      email: '',
      photoPerfil: '',
      fotoPerfil: '',
      descricao: '',
      hasHabilit: true,
      loading: false,
    };
    this.onHeandleChange = this.onHeandleChange.bind(this);
    this.onClick = this.onClick.bind(this);
  }

  onHeandleChange({ target }) {
    const { value, name } = target;
    console.log(name);
    this.setState({
      [name]: value,
    }, () => this.onCountClick());
  }

  onCountClick() {
    const { nome } = this.state;
    const MAX = 3;
    if (nome.length < MAX) {
      this.setState((prevStation) => ({ ...prevStation, hasHabilit: true }));
    } else {
      this.setState((prevStation) => ({ ...prevStation, hasHabilit: false }));
    }
  }

  async onClick() {
    const { nome, email, photoPerfil, descricao } = this.state;
    const { history } = this.props;
    this.setState((prevStation) => ({ ...prevStation, loading: true }));
    await createUser({ name: nome, email, image: photoPerfil, description: descricao });
    history.push('/search');
  }

  render() {
    const { nome, hasHabilit, loading, email, photoPerfil, descricao } = this.state;
    return (

      <div data-testid="page-login">
        {!loading ? (
          <>
            <label htmlFor="nome">
              Nome
              <input
                type="text"
                name="nome"
                id="nome"
                data-testid="login-name-input"
                value={ nome }
                onChange={ this.onHeandleChange }
              />
            </label>
            <label htmlFor="email">
              Email
              <input
                type="email"
                id="email"
                name="email"
                value={ email }
                onChange={ this.onHeandleChange }
              />
            </label>
            <label htmlFor="photoPerfil">
              photoPerfil
              <input
                type="text"
                id="photoPerfil"
                name="photoPerfil"
                value={ photoPerfil }
                onChange={ this.onHeandleChange }
              />
            </label>
            <label htmlFor="descricao">
              Descric√£o:
              <textarea
                id="descricao"
                name="descricao"
                value={ descricao }
                onChange={ this.onHeandleChange }
              />
            </label>
            <Button
              disabled={ hasHabilit }
              id="submit"
              variant="success"
              data-testid="login-submit-button"
              onClick={ this.onClick }
            >
              entrar
            </Button>

          </>
        ) : <Loading />}
      </div>

    );
  }
}
Login.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func,
  }).isRequired,
};
export default Login;
