import React from 'react';
import Header from '../components/Header';
import MusicCard from '../components/MusicCard';
import { getFavoriteSongs } from '../services/favoriteSongsAPI';
import Loading from './Loading';

class Favorites extends React.Component {
  constructor() {
    super();
    this.state = {
      isLoading: true,
      checked: false,
      listFav: [],
    };
    this.onChangeAtualizaList = this.onChangeAtualizaList.bind(this);
  }

  async componentDidMount() {
    this.setState({ isLoading: true });
    const listFav = await getFavoriteSongs();
    console.log(listFav);
    this.setState({ isLoading: false, listFav });
  }

  onChangeAtualizaList({ target }) {
    const { checked } = target;
    this.setState({
      checked,
    });
  }

  render() {
    const { isLoading, listFav } = this.state;
    return (
      <div data-testid="page-favorites">
        <p>Favorites</p>
        <Header />
        <div>
          {
            isLoading ? <Loading /> : listFav.map((music, index) => (
              <MusicCard
                key={ index }
                trackName={ music.trackName }
                previewUrl={ music.previewUrl }
                trackId={ music.trackId }
                id={ music }
                value={ checked }
                onChange={ this.onChangeAtualizaList }
                favorito
              />

            ))
          }

        </div>
      </div>);
  }
}
export default Favorites;
