import React from 'react';
import PropTypes from 'prop-types';
import Carregando from './Carregando';
import getMusics from '../services/musicsAPI';

class MusicCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      id: props.propsId,
      array: [],
      loading: true,
      artist: [],
    };
  }

  async componentDidMount() {
    const { id } = this.state;
    await getMusics(id).then((e) => {
      console.log(e);
      this.setState({ loading: false, array: e, artist: e[0] });
    });
  }

  render() {
    const { array, loading, artist } = this.state;

    return (
      <header>
        <h2>MusicCard</h2>
        <h2 data-testid="artist-name">{artist.artistName}</h2>
        <h3 data-testid="album-name">{artist.collectionName}</h3>
        {loading && <Carregando />}
        {array.length > 0 && array.map((e, index) => (
          index !== 0
          && (
            <div key={ index }>
              <p>{e.trackNumber }</p>
              <audio data-testid="audio-component" src={ e.previewUrl } controls>
                <track kind="captions" />
                O seu navegador n√£o suporta o elemento
                <code>audio</code>
              </audio>
              <label
                htmlFor="favorito"
                data-testid={ `checkbox-music-${e.trackNumber}` }
              >
                Favorita
                <input
                  type="checkbox"
                  name="favorito"
                  id="favorito"
                />
              </label>
            </div>)
        ))}

      </header>

    );
  }
}

MusicCard.propTypes = {
  propsId: PropTypes.string.isRequired,
};

export default MusicCard;
