import React from 'react';
import PropTypes from 'prop-types';
import { createUser } from '../services/userAPI';
import Loading from './Loading';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      nome: '',
      email: '',
      hasHabilit: true,
      loading: false,
    };
    this.onHeandleChange = this.onHeandleChange.bind(this);
    this.onClick = this.onClick.bind(this);
  }

  onHeandleChange({ target }) {
    const { value, name } = target;
    this.setState({
      nome: value,
    }, () => this.onCountClick());
  }

  onCountClick() {
    const { nome } = this.state;
    const MAX = 3;
    if (nome.length < MAX) {
      this.setState((prevStation) => ({ ...prevStation, hasHabilit: true }));
    } else {
      this.setState((prevStation) => ({ ...prevStation, hasHabilit: false }));
    }
  }

  async onClick() {
    const { nome } = this.state;
    const { history } = this.props;
    this.setState((prevStation) => ({ ...prevStation, loading: true }));
    await createUser({ name: nome });
    history.push('/search');
  }

  render() {
    const { nome, hasHabilit, loading } = this.state;
    return (

      <div data-testid="page-login">
        {!loading ? (
          <>
            <label htmlFor="nome">
              Nome
              <input
                type="text"
                name="nome"
                id="nome"
                data-testid="login-name-input"
                value={ nome }

              />
            </label>
            <label htmlFor="email">
              Email
              <input
                type="email"
                id="email"
                name="email"
                value={ email }
                onChange={ this.onHeandleChange }
              />
            </label>
            <button
              disabled={ hasHabilit }
              type="button"
              id="submit"
              data-testid="login-submit-button"
              onClick={ this.onClick }
            >
              entrar
            </button>

          </>
        ) : <Loading />}
      </div>

    );
  }
}
Login.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func,
  }).isRequired,
};
export default Login;
