import React from 'react';
import Header from '../components/Header';
import MusicCard from '../components/MusicCard';
import getMusics from '../services/musicsAPI';
import Loading from './Loading';

class Album extends React.Component {
  constructor() {
    super();
    this.state = {
      albumId: '',
      isLoading: true,
      listCantor: [],
      listMusic: [],
    };
  }

  async componentDidMount() {
    const { match } = this.props;
    const { params } = match;
    const { id } = params;
    this.setState({ albumId: id, isLoading: true });
    const music = await getMusics(id);
    const musicList = music.filter(({ kind }) => kind === 'song');
    this.setState({ albumId: id,
      isLoading: false,
      listCantor: music[0],
      listMusic: musicList });
    console.log(music);
  }

  render() {
    const { albumId, isLoading, listCantor, listMusic } = this.state;
    return (
      <div data-testid="page-album">
        <Header />
        <h2 data-testid="artist-name">{listCantor.artistName}</h2>
        <h3 data-testid="album-name">{listCantor.collectionName}</h3>
        { isLoading ? <Loading />
          : (listMusic.map((music, index) => (<MusicCard
              trackName={ music.trackName }
              key={ index }
              previewUrl={ music.previewUrl }
          />)))}
      </div>);
  }
}
export default Album;
