import React from 'react';
import { createUser } from '../services/userAPI';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      nome: '',
      hasHabilit: true,
    };
    this.onHeandleChange = this.onHeandleChange.bind(this);
  }

  onHeandleChange({ target }) {
    const { value } = target;
    this.setState({
      nome: value,
    }, () => this.onCountClick());
  }

  onCountClick() {
    const { nome } = this.state;
    const MAX = 3;
    if (nome.length <= MAX) {
      this.setState((prevStation) => ({ ...prevStation, hasHabilit: true }));
    } else {
      this.setState((prevStation) => ({ ...prevStation, hasHabilit: false }));
    }
  }

  async onClick() {
    const { nome } = this.state;
    await createUser({ name: nome });
  }

  render() {
    const { nome, hasHabilit } = this.state;
    return (
      hasHabilit
        ? <div data-testid="page-login">
          <label htmlFor="nome">
            Nome
            <input
              type="text"
              name="nome"
              id="nome"
              data-testid="login-name-input"
              value={ nome }
              onChange={ this.onHeandleChange }
            />
          </label>
          <button
            disabled={ hasHabilit }
            type="submit"
            id="submit"
            data-testid="login-submit-button"
            onClick={ this.onClick }
          >
            entrar
          </button>

        </div>
        : <span>Loading</span>
    );
  }
}
export default Login;
