import React from 'react';
import Header from '../components/Header';

class Search extends React.Component {
  constructor() {
    super();
    this.state = {
      hasDiseble: true,
    };
    this.handleChageInput = this.handleChageInput.bind(this);
  }

  handleChageInput({ target }) {
    const nameArtist = target.value;
    if (nameArtist <= 2) {
      this.setState({ hasDiseble: true });
    } else {
      this.setState({ hasDiseble: false });
    }
  }

  render() {
    const { hasDiseble } = this.state;
    return (
      <div data-testid="page-search">

        <Header />
        <h1>search</h1>
        <label htmlFor="artistInput">
          <input
            data-testid="search-artist-input"
            type="text"
            name="artistInput"
            onChange={ this.handleChageInput }
          />
        </label>
        <input
          data-testid="search-artist-button"
          type="button"
          value="Pesquisar"
          disabled={ hasDiseble }
        />
      </div>);
  }
}
export default Search;
