import React from 'react';
import Header from '../components/Header';
import getMusics from '../services/musicsAPI';
import Loading from './Loading';

class Album extends React.Component {
  constructor() {
    super();
    this.state = {
      albumId: '',
      isLoading: true,
      listMusica: [],
    };
  }

  async componentDidMount() {
    const { match: { params: { id } } } = this.props;
    this.setState({ albumId: id, isLoading: true });
    const music = await getMusics(id);
    this.setState({ albumId: id, isLoading: false, listMusica: music });
    console.log(music[0].artistName);
  }

  render() {
    const { albumId, isLoading, listMusica } = this.state;
    return (
      <div data-testid="page-album">
        <Header />
        {isLoading ? <Loading />
          : <h2 data-testid="artist-name">{listMusica[0].artistName}</h2>}
      </div>);
  }
}
export default Album;
