import React from 'react';
import Header from '../components/Header';
import MusicCard from '../components/MusicCard';
import { getFavoriteSongs } from '../services/favoriteSongsAPI';
import Loading from './Loading';

class Favorites extends React.Component {
  constructor() {
    super();
    this.state = {
      isLoading: true,
      listFav: [],
    };
  }

  async componentDidMount() {
    this.setState({ isLoading: true });
    const listFav = await getFavoriteSongs();
    this.setState({ isLoading: false, listFav });
  }

  componentDidUpdate() {
    document.getElementById('mydiv').innerHTML = `The updated favorite is ${this.state.favoritecolor}`;
  }

  render() {
    return (
      <div>
        <h1>
          My Favorite Color is
          {' '}
          {this.state.favoritecolor}
        </h1>
        <div id="mydiv" />
      </div>
    );
  }

  render() {
    const { isLoading, listFav } = this.state;
    return (
      <div data-testid="page-favorites">
        <p>Favorites</p>
        <Header />
        <div>
          {
            isLoading ? <Loading /> : listFav.map((music, index) => (
              <MusicCard
                key={ index }
                trackName={ music.trackName }
                previewUrl={ music.previewUrl }
                trackId={ music.trackId }
                id={ music }
                favorito
              />

            ))
          }

        </div>
      </div>);
  }
}
export default Favorites;
