import React from 'react';
import PropTypes from 'prop-types';
import Header from '../components/Header';
import MusicCard from '../components/MusicCard';
import getMusics from '../services/musicsAPI';
import Loading from './Loading';
import { getFavoriteSongs } from '../services/favoriteSongsAPI';

class Album extends React.Component {
  constructor() {
    super();
    this.state = {
      isLoading: true,
      listCantor: [],
      listMusic: [],
      musicaFav: [],
    };
    this.atualizaSong = this.atualizaSong.bind(this);
  }

  async componentDidMount() {
    const { match } = this.props;
    const { params } = match;
    const { id } = params;

    this.setState({ isLoading: true });
    const music = await getMusics(id);
    const musicList = music.filter(({ kind }) => kind === 'song');
    const musicFav = await atualizaSong();
    console.log(musicFav);
    this.setState({
      isLoading: false,
      listCantor: music[0],
      listMusic: musicList,
    });
  }

  async atualizaSong() {
    this.setState({ isLoading: true });
    await getFavoriteSongs();
    this.setState({ isLoading: false, musicaFav: musicFav });
  }

  render() {
    const { isLoading, listCantor, listMusic, musicaFav } = this.state;
    return (
      <div data-testid="page-album">
        <Header />
        <h2 data-testid="artist-name">{listCantor.artistName}</h2>
        <h3 data-testid="album-name">{listCantor.collectionName}</h3>
        { isLoading ? <Loading />
          : (listMusic.map((music, index) => (
            <MusicCard
              trackName={ music.trackName }
              key={ index }
              previewUrl={ music.previewUrl }
              trackId={ music.trackId }
              id={ music }
              favorito={ musicaFav.some((m) => m.trackId === music.trackId) }
              isAlbum
              atualizaSong={ this.atualizaSong }
            />
          )))}
      </div>);
  }
}
Album.propTypes = {
  match: PropTypes.shape({
    params: PropTypes.shape({
      id: PropTypes.string.isRequired,
    }).isRequired,
  }).isRequired,
};
export default Album;
