import React from 'react';
import PropTypes from 'prop-types';
import Carregando from './Carregando';
import getMusics from '../services/musicsAPI';

class MusicCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      id: props.propsId,
      array: [],
      loading: true,

    };
  }

  async componentDidMount() {
    const { id } = this.state;
    await getMusics(id).then((e) => {
      this.setState({ loading: false, array: e });
    });
  }

  render() {
    const { array, loading } = this.state;

    return (
      <header>
        <h2>MusicCard</h2>
        {loading && <Carregando />}
        {array.length > 0 && array.map((e, index) => (
          index !== 0
          && (<div key={ index }>
            <p data-testid="artist-name">{e.artistName}</p>
            <p data-testid="album-name">{e.trackName}</p>
            <audio data-testid="audio-component" src={ e.previewUrl } controls>
              <track kind="captions" />
              O seu navegador n√£o suporta o elemento
              <code>audio</code>
            </audio>
            <label
              htmlFor="favorito"
              data-testid={ `checkbox-music-${e.trackId}` }
            >
              Favorita
              <input
                type="checkbox"
                name="favorito"
              />
            </label>
              </div>)
        ))}

      </header>

    );
  }
}

MusicCard.propTypes = {
  propsId: PropTypes.number.isRequired,
};

export default MusicCard;
