import React from 'react';
import PropTypes from 'prop-types';
import 'bootstrap/dist/css/bootstrap.min.css';
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { createUser } from '../services/userAPI';
import Loading from './Loading';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      nome: '',
      email: '',
      photoPerfil: '',
      fotoPerfil: '',
      descricao: '',
      hasHabilit: true,
      loading: false,
    };
    this.onHeandleChange = this.onHeandleChange.bind(this);
    this.onClick = this.onClick.bind(this);
  }

  onHeandleChange({ target }) {
    const { value, name } = target;
    console.log(name);
    this.setState({
      [name]: value,
    }, () => this.onCountClick());
  }

  onCountClick() {
    const { nome } = this.state;
    const MAX = 3;
    if (nome.length < MAX) {
      this.setState((prevStation) => ({ ...prevStation, hasHabilit: true }));
    } else {
      this.setState((prevStation) => ({ ...prevStation, hasHabilit: false }));
    }
  }

  async onClick() {
    const { nome, email, photoPerfil, descricao } = this.state;
    const { history } = this.props;
    this.setState((prevStation) => ({ ...prevStation, loading: true }));
    await createUser({ name: nome, email, image: photoPerfil, description: descricao });
    history.push('/search');
  }

  render() {
    const { nome, hasHabilit, loading, email, photoPerfil, descricao } = this.state;
    return (

      <div data-testid="page-login">

        {!loading ? (
          <Form>
            <Form.Group className="mb-3" controlId="formBasicName">
              <Form.Label htmlFor="nome">
                Nome
                <Form.Control
                  type="text"
                  name="nome"
                  id="nome"
                  data-testid="login-name-input"
                  value={ nome }
                  onChange={ this.onHeandleChange }
                />
              </Form.Label>
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicEmail">
              <Form.Label htmlFor="email">
                Email
                <Form.Control
                  type="email"
                  id="email"
                  name="email"
                  value={ email }
                  onChange={ this.onHeandleChange }
                />
              </Form.Label>
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicPhotoPerfil">
              <Form.Label htmlFor="photoPerfil">
                Foto Perfil
                <Form.Control
                  type="text"
                  id="photoPerfil"
                  name="photoPerfil"
                  value={ photoPerfil }
                  onChange={ this.onHeandleChange }
                />
              </Form.Label>
            </Form.Group>
            <Form.Group className="mb-3" controlId="formBasicDesc">
              <Form.Label htmlFor="descricao">
                Descric√£o:
                <Form.Control
                  as="textarea"
                  rows={ 3 }
                  id="descricao"
                  name="descricao"
                  value={ descricao }
                  onChange={ this.onHeandleChange }
                />
              </Form.Label>
            </Form.Group>
            <Button
              disabled={ hasHabilit }
              id="submit"
              as="submit"
              variant="primary"
              data-testid="login-submit-button"
              onClick={ this.onClick }
              size="lg"
            >
              ENTRAR
            </Button>
          </Form>
        ) : <Loading />}
      </div>

    );
  }
}
Login.propTypes = {
  history: PropTypes.shape({
    push: PropTypes.func,
  }).isRequired,
};
export default Login;
