import React from 'react';
import Header from '../components/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';

class Search extends React.Component {
  constructor() {
    super();
    this.state = {
      nameArtist: '',
      hasDiseble: true,
      listAlbum: [],
      hasLoading: false,
    };
    this.handleChageInput = this.handleChageInput.bind(this);
    this.onCountClick = this.onCountClick.bind(this);
    this.onClickButton = this.onClickButton.bind(this);
  }

  handleChageInput({ target }) {
    const name = target.value;
    this.setState({
      nameArtist: name,
    }, () => this.onCountClick());
  }

  onCountClick() {
    const { nameArtist } = this.state;
    if (nameArtist.length < 2) {
      this.setState((prevStation) => ({ ...prevStation, hasDiseble: true }));
    } else {
      this.setState((prevState) => ({ ...prevState, hasDiseble: false }));
    }
  }

  async onClickButton() {
    const { nameArtist } = this.state;
    this.setState((prevState) => ({ ...prevState, nameArtist: '', hasLoading: false }));
    const album = await searchAlbumsAPI(nameArtist);
    console.log(album);
  }

  render() {
    const { hasDiseble, nameArtist } = this.state;
    return (
      <div data-testid="page-search">

        <Header />
        <h1>search</h1>
        <label htmlFor="artistInput">
          <input
            data-testid="search-artist-input"
            type="text"
            name="artistInput"
            value={ nameArtist }
            onChange={ this.handleChageInput }
          />
        </label>
        <button
          data-testid="search-artist-button"
          type="button"
          disabled={ hasDiseble }
          onClick={ this.onClickButton }
        >
          Pesquisar
        </button>
      </div>);
  }
}
export default Search;
