import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav, Container } from 'react-bootstrap';

import Loading from '../pages/loading/Loading';
import { getUser } from '../services/userAPI';
import './Header.css';

class Header extends React.Component {
  constructor() {
    super();
    this.state = {
      name: '',
      loading: true,
    };
  }

  async componentDidMount() {
    const ususario = await getUser();
    console.log(ususario.name);
    this.setState({
      loading: false,
      name: ususario.name,
    });
  }

  render() {
    const { name, loading } = this.state;
    return (
      <div>
        <header>
          <Navbar collapseOnSelect expand="lg" className="backgrung-nav">
            <Container>
              <Navbar.Brand href="/">TrybeTunes</Navbar.Brand>
              <Navbar.Toggle aria-controls="responsive-navbar-nav" />
              <Navbar.Collapse id="responsive-navbar-nav">
                <Nav className="AllLinks">
                  {' '}
                  <Nav.Link
                    className="navLiks"
                    eventKey={ 2 }
                    href="/search"
                  >
                    Search
                  </Nav.Link>
                  <Nav.Link className="navLiks" eventKey={ 2 }>
                    Favorites
                  </Nav.Link>
                  <Nav.Link className="navLiks" eventKey={ 2 } href="/profile">
                    Profile
                  </Nav.Link>
                </Nav>
              </Navbar.Collapse>
            </Container>
          </Navbar>
        </header>
        {
          loading ? <Loading />
            : (
              <h1 className="titleName">
                Bem-vindo(a)
                {' '}
                {name}
              </h1>
            )
        }
      </div>
    );
  }
}
export default Header;
