import React from 'react';
import Header from '../components/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';
import Loading from './Loading';

class Search extends React.Component {
  constructor() {
    super();
    this.state = {
      nameArtist: '',
      hasDiseble: true,
      listAlbum: [],
      hasLoading: true,
    };
    this.handleChageInput = this.handleChageInput.bind(this);
    this.onCountClick = this.onCountClick.bind(this);
    this.onClickButton = this.onClickButton.bind(this);
  }

  handleChageInput({ target }) {
    const name = target.value;
    this.setState({
      nameArtist: name,
    }, () => this.onCountClick());
  }

  onCountClick() {
    const { nameArtist } = this.state;
    if (nameArtist.length < 2) {
      this.setState((prevStation) => ({ ...prevStation, hasDiseble: true }));
    } else {
      this.setState((prevState) => ({ ...prevState, hasDiseble: false }));
    }
  }

  async onClickButton() {
    this.setState({ nameArtist: '', hasLoading: false });
    const album = await searchAlbumsAPI(nameArtist);
    this.setState({ nameArtist: '', hasLoading: true });
    console.log(album);
  }

  render() {
    const { hasDiseble, nameArtist, hasLoading } = this.state;
    return (
      <div data-testid="page-search">

        <Header />

        <h1>search</h1>
        <label htmlFor="artistInput">
          <input
            data-testid="search-artist-input"
            type="text"
            name="artistInput"
            value={ nameArtist }
            onChange={ this.handleChageInput }
          />
        </label>
        <button
          data-testid="search-artist-button"
          type="button"
          disabled={ hasDiseble }
          onClick={ this.onClickButton }
        >
          Pesquisa
        </button>
        {
          !hasLoading
            ? <h1>
              Resultado de Ã¡lbuns de
              {' '}
              { nameArtist }
              </h1>
            : <Loading />
        }
      </div>);
  }
}
export default Search;
