import React from 'react';
import { Link } from 'react-router-dom';
import { getUser } from '../services/userAPI';
import Carregando from './Carregando';

class Header extends React.Component {
  constructor() {
    super();
    this.state = {
      nome: '',
      loading: true,
    };
  }

  componentDidMount() { // requisito 3
    getUser().then((e) => {
      if (e.name) { // se o que estiver no imput for verdadeiro muda o estado loading, na linha 29 confere e coloca na tela o carregando
        this.setState({ loading: false }); // muda o estado loading e recebe o nome.
      }
      this.setState({ nome: e.name });
    });
  }

  render() {
    const { nome, loading } = this.state;
    return (

      <main>
        {loading && <Carregando /> }

        {/* {<p>requisito 4 </p>} */}

        <header data-testid="header-component">
          <Link data-testid="link-to-search" to="/search"> Search </Link>
          <Link data-testid="link-to-favorites" to="/favorites"> Favorites</Link>
          <Link data-testid="link-to-profile" to="/profile"> Profile </Link>
          <p data-testid="header-user-name">
            { nome }
          </p>

        </header>
      </main>
    );
  }
}

export default Header;
