import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Navbar, Nav, Container } from 'react-bootstrap';

import Loading from '../pages/Loading';
import { getUser } from '../services/userAPI';
import './Header.css';

class Header extends React.Component {
  constructor() {
    super();
    this.state = {
      name: '',
      loading: true,
    };
  }

  async componentDidMount() {
    const ususario = await getUser();
    console.log(ususario.name);
    this.setState({
      loading: false,
      name: ususario.name,
    });
  }

  render() {
    const { name, loading } = this.state;
    return (
      <div>
        <Navbar collapseOnSelect expand="lg" className="backgrung-nav">
          <Container>
            <Navbar.Brand href="/">TrybeTunes</Navbar.Brand>
            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
            <Navbar.Collapse id="responsive-navbar-nav">
              <Nav>
                {' '}
                <Nav.Link className="navLiks" eventKey={ 2 } href="/search">
                  Search
                </Nav.Link>
                <Nav.Link className="navLiks" eventKey={ 2 } href="/favorites">
                  Favorites
                </Nav.Link>
                <Nav.Link className="navLiks" eventKey={ 2 } href="/profile">
                  Profile
                </Nav.Link>
              </Nav>
            </Navbar.Collapse>
          </Container>
        </Navbar>
        {
          loading ? <Loading />
            : (
              <h1>{name}</h1>
            )
        }
      </div>
    );
  }
}
export default Header;
