import React from 'react';
import PropTypes from 'prop-types';
import { addSong, removeSong } from '../services/favoriteSongsAPI';
import Loading from '../pages/loading/Loading';
import './musicaCard.css';

class MusicCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isFaforite: false,
    };
    this.onChangeInput = this.onChangeInput.bind(this);
    this.addFavoritos = this.addFavoritos.bind(this);
  }

  componentDidMount() {
    const { favorito } = this.props;
    if (favorito) {
      this.setState({
        isFaforite: true,
      });
    }
    const script = document.createElement('script');
    script.src = './script.js';
    script.async = true;
    script.document = document;
    document.body.appendChild(script);
  }

  onChangeInput({ target }) {
    const { checked } = target;
    this.setState({
      isFaforite: checked,
    }, () => this.addFavoritos());
  }

  async addFavoritos() {
    const { isFaforite } = this.state;
    const { id, atualizaSong, isAlbum } = this.props;

    if (isFaforite) {
      this.setState({ isLoading: true });
      await addSong(id);
      this.setState({ isLoading: false });
    }
    if (!isFaforite) {
      this.setState({ isLoading: true });
      await removeSong(id);
      this.setState({ isLoading: false });
      if (!isAlbum) {
        await atualizaSong();
      }
    }
  }

  render() {
    const { previewUrl, trackName, trackId } = this.props;
    const { isLoading, isFaforite } = this.state;
    return (
      <div>
        { isLoading
          ? (<Loading />)
          : (
            /*
            <div>
              <p>{trackName}</p>
              <audio data-testid="audio-component" src={ previewUrl }  preload="nonde" controls>
                <track kind="captions" />
                O seu navegador não suporta o elemento
                <code>audio</code>
              </audio>
              <label htmlFor={ trackId }>
                Favorita
                <input
                  type="checkbox"
                  id={ trackId }
                  data-testid={ `checkbox-music-${trackId}` }
                  checked={ isFaforite }
                  atualizaSong={ this.atualizaSong }
                  onChange={ this.onChangeInput }
                />
              </label>
            </div> */
            <div className="player-container">
              <div className="player-wrap">
                <div className="play-pause" />
                <section className="song-meta">
                  <div className="artist">{trackName}</div>
                  <div className="timeline" />
                  <progress className="pcast-progress" value="0" />
                  <span className="pcast-currenttime pcast-time">0:00</span>
                </section>
                <audio
                  id="main-audio"
                  preload="none"
                  width="100%"
                  src={ previewUrl }
                >
                  <source src={ previewUrl } type="audio/mpeg" />
                </audio>
              </div>
            </div>

          )}
      </div>
    );
  }
}
MusicCard.propTypes = {
  previewUrl: PropTypes.string.isRequired,
  trackName: PropTypes.string.isRequired,
  trackId: PropTypes.number.isRequired,
  favorito: PropTypes.bool.isRequired,
  id: PropTypes.shape().isRequired,
  atualizaSong: PropTypes.func.isRequired,
  isAlbum: PropTypes.bool.isRequired,
};
export default MusicCard;
