import React from 'react';

class Login extends React.Component {
  constructor() {
    super();
    this.state = {
      login: '',
    };
  }

  onHeandleChange({ target }) {
    this.setState({
      login: target.value,
    });
  }

  render() {
    return (
      <div data-testid="page-login">
        <label htmlFor="nome">
          Nome
          <input
            type="text"
            name="nome"
            id="nome"
            data-testid="login-name-input"
            onChange={ this.onHeandleChange() }
          />
        </label>
        <button type="submit" id="submit" data-testid="login-submit-button">
          entrar
        </button>
      </div>
    );
  }
}
export default Login;
