import React from 'react';
import Header from '../components/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';
import Loading from './Loading';

class Search extends React.Component {
  constructor() {
    super();
    this.state = {
      nameArtist: '',
      hasDiseble: true,
      hasLoading: false,
      habilitInput: false,
      albumArtist: [],
    };
    this.handleChageInput = this.handleChageInput.bind(this);
    this.onCountClick = this.onCountClick.bind(this);
    this.onClickButton = this.onClickButton.bind(this);
  }

  handleChageInput({ target }) {
    const name = target.value;

    this.setState({
      nameArtist: name,
    }, () => this.onCountClick());
  }

  onCountClick() {
    const { nameArtist } = this.state;
    if (nameArtist.length < 2) {
      this.setState({ hasDiseble: true });
    } else {
      this.setState({ hasDiseble: false });
    }
  }

  async onClickButton() {
    const { nameArtist } = this.state;
    const name = nameArtist;
    this.setState({ nameArtist: '', hasLoading: false });
    const albumList = await searchAlbumsAPI(nameArtist);

    const filterAlbum = albumList.filter((artist) => {
      const artistLowar = artist.artistName.toLowerCase();
      const namePesq = name.toLowerCase();
      return artistLowar === namePesq;
    });
    this.setState({ albumArtist: filterAlbum, arhasLoading: true });
    console.log(filterAlbum);
  }

  render() {
    const { hasDiseble, nameArtist, hasLoading, habilitInput } = this.state;
    return (
      <div data-testid="page-search">

        <Header />

        <h1>search</h1>
        <label htmlFor="artistInput">
          <input
            data-testid="search-artist-input"
            type="text"
            name="artistInput"
            value={ nameArtist }
            disabled={ habilitInput }
            onChange={ this.handleChageInput }
          />
        </label>
        <button
          data-testid="search-artist-button"
          type="button"
          disabled={ hasDiseble }
          onClick={ this.onClickButton }
        >
          Pesquisa
        </button>
      </div>);
  }
}
export default Search;
