import React from 'react';
import Header from '../components/Header';
import MusicCard from '../components/MusicCard';
import { getFavoriteSongs } from '../services/favoriteSongsAPI';
import getMusics from '../services/musicsAPI';
import Loading from './Loading';

class Favorites extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLoading: true,
      listFav: '',
      listMusic: '',
    };
  }

  async componentDidMount() {
    this.setState({ isLoading: true });
    const listFav = await getFavoriteSongs();
    const listMusic = await getMusics(listFav[0].artistId);
    console.log(listFav);
    console.log(listMusic);
    this.setState({ isLoading: false, listFav, listMusic });
  }

  render() {
    const { isLoading, listFav, listMusic } = this.state;
    return (
      <div data-testid="page-favorites">
        <p>Favorites</p>
        <Header />
        <div>
          {
            isLoading ? <Loading /> : listFav.filter((music, index) => (
              music.trackId
              == <MusicCard
                key={ index }
                trackName={ music.trackName }
                previewUrl={ music.previewUrl }
                trackId={ music.trackId }
                id={ music }
                favorito
              />

            ))
          }

        </div>
      </div>);
  }
}
export default Favorites;
