import React from 'react';
import Header from '../components/Header';
import searchAlbumsAPI from '../services/searchAlbumsAPI';
import Loading from './Loading';

class Search extends React.Component {
  constructor() {
    super();
    this.state = {
      artist: '',
      hasDiseble: true,
      hasLoading: false,
      habilitInput: false,
      name: '',
      albumArtist: [],
    };
    this.handleChageInput = this.handleChageInput.bind(this);
    this.onCountClick = this.onCountClick.bind(this);
    this.onClickButton = this.onClickButton.bind(this);
  }

  handleChageInput({ target }) {
    const name = target.value;

    this.setState({
      artist: name,
    }, () => this.onCountClick());
  }

  onCountClick() {
    const { artist } = this.state;
    if (artist.length < 2) {
      this.setState({ hasDiseble: true });
    } else {
      this.setState({ hasDiseble: false });
    }
  }

  async onClickButton() {
    const { artist } = this.state;
    const name = artist;
    this.setState({ artist: '', name, hasLoading: true });
    const albumList = await searchAlbumsAPI(artist);

    this.setState({ hasLoading: false, albumArtist: albumList });
    console.log(albumList);
  }

  render() {
    const { hasDiseble,
      nameArtist, hasLoading, habilitInput,
      name, albumArtist } = this.state;
    return (
      <div data-testid="page-search">

        <Header />

        <h1>search</h1>
        {hasLoading ? <Loading />
          : (<>
            <label htmlFor="artistInput">
              <input
                data-testid="search-artist-input"
                type="text"
                name="artistInput"
                value={ nameArtist }
                disabled={ habilitInput }
                onChange={ this.handleChageInput }
              />
            </label>
            <button
              data-testid="search-artist-button"
              type="button"
              disabled={ hasDiseble }
              onClick={ this.onClickButton }
            >
              Pesquisa
            </button>
            <h1>
              Resultado de Ã¡lbuns de:
              {' '}
              {name}
            </h1>
            <div>

              {albumArtist.map((artist) => {
                <ul>
                  <li>artist.artistName</li>
                  <li><img src={ artist.artworkUrl100 } alt={ artist.artistName } /></li>
                </ul>;
              })}

            </div>
          </>)}
      </div>);
  }
}
export default Search;
